import React, {useState} from 'react';

const todo = (props) => {
  const inputState = useState();

  return (
    <React.Fragment>
      <input type="text" placeholder="Todo" onChange={inputState[1]} value={inputState[0]} />
      <button type="button">Add</button>
      <ul></ul>
    </React.Fragment>
  );
};

export default todo;
