const initialState = {
  counter: 0,
};

const reducer = (state = initialState, action) => {
//   if (action.type === 'INCREMENT') {
//     return {
//       counter: state.counter + 1,
//     };
//   }
//   return state;
    switch (action.type) {
      case 'INCREMENT':
        return {
          counter: state.counter + 1,
        };
      case 'DECREMENT':
          return{
              ...state,
              counter: state.counter - 1,
          }

      default:
        return state;
    }
};

export default reducer;
