const initialState = {
  counter: 0,
  results: [],
};

const reducer = (state = initialState, action) => {
  console.log(action);
  switch (action.type) {
    case 'INCREMENT':
      const newState = Object.assign({}, state);
      newState.counter = state.counter + 1;
      return newState;
      // return {
      //   counter: state.counter + 1,
      // };
    case 'DECREMENT':
      console.log('DECREMENT Clicked');
      console.log('DECREMENT Action Properties', action);
      return {
        ...state,
        counter: state.counter - 1,
      };

    case 'ADD':
    //  console.log('ADD Clicked');
    //  console.log('ADD Action Properties', action);
      return {
        ...state,
        counter: state.counter + action.val,
      };

    case 'SUBTRACT':
    //  console.log('SUBTRACT Clicked');
    //  console.log('SUBTRACT Action Properties', action);
      return {
        ...state,
        counter: state.counter - action.val,
      };

    case 'STORE_RESULT':
     console.log('STORE_RESULT Clicked');
     console.log(...state);
      return {...state, results: state.results.concat({id: new Date(),value:state.counter})};

    default:
      return state;
  }
};

export default reducer;
